import React, { Modal, Button } from 'react';
import unsplash from '../api/unsplash';
import SearchBar from './SearchBar';
import ImageList from './ImageList';
import Header from './Header';
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

class App extends React.Component {
    state = { images: [], loading: false, popup: false };

    async onSearchSubmit(term) {
        this.setState({ loading: true });

        const response = await unsplash.get('/search/photos', {
            params: {
                query: term
            },
        })

        this.setState({
            images: response.data.results,
            loading: false
        })
    }

    showPopup() {
        this.setState({ popup: true })
    }

    closePopup() {
        this.setState({ popup: false })
    }

    render() {
        return (
            <div className="ui container" style={{ marginTop: '10px' }}>
                <Header onPostClick={() => this.showPopup()} />
                <SearchBar onSubmit={this.onSearchSubmit.bind(this)} loading={this.state.loading} />
                <ImageList images={this.state.images} />
                {this.state.popup ? <Modal setShowModal={() => this.closePopup()} /> : null}
            </div>
        )
    }
}

export default App
