import React from 'react';
import styled from "styled-components";

const Button = styled.button`
  background-color: #34abeb;
  color: white;
  padding: 5px 15px;
  border-radius: 5px;
  outline: 0;
  text-transform: uppercase;
  margin: 10px 0px;
  cursor: pointer;
  box-shadow: 0px 2px 2px lightgray;
  transition: ease background-color 250ms;
  &:hover {
    background-color: #347aeb;
  }
  &:disabled {
    cursor: default;
    opacity: 0.7;
  }
`;

/*

const theme = {
    blue: {
      default: "#3f51b5",
      hover: "#283593"
    },
    pink: {
      default: "#e91e63",
      hover: "#ad1457"
    }
  };

function clickMe() {
    alert("You clicked me!");
}
 */
const PostButton = () => {
    return (
        <Button />
    )
}

export default PostButton;
