import React, {Button} from 'react';
import unsplash from '../api/unsplash';
import SearchBar from './SearchBar';
import ImageList from './ImageList';
import Header from './Header';
import Modal, { ModalBody, ModalFooter, ModalHeader } from './Modal';

class App extends React.Component {
    state = { images: [], loading: false, modal: false };

    async onSearchSubmit(term) {
        this.setState({ loading: true });

        const response = await unsplash.get('/search/photos', {
            params: {
                query: term
            },
        })

        this.setState({
            images: response.data.results,
            loading: false
        })
    }

    showModal() {
        this.setState({ modal: true })
    }

    closeModal() {
        this.setState({ modal: false })
    }

    render() {
        return (
            <div className="ui container" style={{ marginTop: '10px' }}>
                <Header onPostClick={() => this.showPopup()} />
                <SearchBar onSubmit={this.onSearchSubmit.bind(this)} loading={this.state.loading} />
                <ImageList images={this.state.images} />
                <Modal
                show={this.state.modal}
                setShow={this.showModal}
            // hideCloseButton
            >
                <ModalHeader>
                    <h2>Modal header</h2>
                </ModalHeader>
                <ModalBody>
                    <p style={{ textAlign: 'justify' }}>
                        Lorem ipsum dolor sit amet consectetur adipisicing elit. Deserunt maxime dolorem asperiores laboriosam ad delectus ea. Tempora tempore repellendus laudantium fugiat saepe mollitia eius illo possimus laborum consequuntur, tenetur neque.
                    </p>
                </ModalBody>
                <ModalFooter>
                    <Button onClick={() => this.closeModal()}>
                        Close
                    </Button>
                </ModalFooter>
            </Modal>
            </div>
        )
    }
}

export default App
